  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"react-mixitup"}}>{`react-mixitup`}</MDXTag>
<MDXTag name="blockquote" components={components}>
<MDXTag name="p" components={components} parentName="blockquote">{`Animate the addition, removal and shuffling of elements.`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="a" components={components} parentName="p" props={{"href":"https://www.npmjs.com/package/react-mixitup"}}><MDXTag name="img" components={components} parentName="a" props={{"src":"https://img.shields.io/npm/v/react-mixitup.svg","alt":"NPM"}}></MDXTag></MDXTag>{` `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://standardjs.com"}}><MDXTag name="img" components={components} parentName="a" props={{"src":"https://img.shields.io/badge/code_style-standard-brightgreen.svg","alt":"JavaScript Style Guide"}}></MDXTag></MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"install"}}>{`Install`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-bash"}}>{`npm install --save react-mixitup
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"usage"}}>{`Usage`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-tsx"}}>{`import * as React from 'react'
import ReactMixitup from 'react-mixitup'
import { shuffle, range } from 'lodash'

class Example extends React.Component {
  state = { items: [1, 2, 3, 4] }

  shuffle = () =>
    this.setState({
      items: shuffle(range(Math.round(Math.random() * 15)))
    })

  render() {
    return (
      <Fragment>
        <button onClick={this.shuffle}>Shuffle</button>
        <ReactMixitup
          items={this.state.items}
          renderCells={items => (
            <div style={{ background: 'yellow' }}>
              {items.map(({ key, ref, style }) => (
                <div key={key} ref={ref} style={{ ...style, background: 'red' }}>
                  {key}
                </div>
              ))}
            </div>
          )}
        />
      </Fragment>
    )
  }
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"license"}}>{`License`}</MDXTag>
<MDXTag name="p" components={components}>{`MIT Â© `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://github.com/ricsam"}}>{`ricsam`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
MDXContent.isMDXComponent = true
